import React from 'react';
import './App.css';
import Profile from './components/Profile';

class App extends React.Component {
  state = {
    loading: true,
    name: '',
    image: '',
    email: '',
    age: '',
  };




  render () {
    const { loading } = this.state;

    return (
      <div>
        <h1>Random Person Profile</h1>
        { loading? <p>Loading...</p> :
        <Profile
          { ...this.state }
        />
        }
      </div>
    );
    }
}

export default App;
