import React from 'react';
import './App.css';
import Profile from './components/Profile';

class App extends React.Component {
  state = {
    loading: true,
    name: '',
    image: '',
    email: '',
    age: '',
  };

  fetchProfile = async () => {
    const requestReturn = await fetch('https://api.randomuser.me/');
    const requestObject = await requestReturn.json();
    const result = {...requestObject.results[0]};
    const {
      name: { 
        first,
        last,
        title
      }, 
      email,
      dob: {
        age
      },
      picture: {
        large,
      }
    } = result;

    this.setState({
      name: `${title} ${first} ${last}`,
      image: large,
      email: email,
      age: age,
    });
  };

  componentDidMount = () => {
    this.fetchProfile();
  };


  render () {
    return (
      <div>
        <h1>Random Person Profile</h1>
        <Profile
          { ...this.state }
        />
      </div>
    );
    }
}

export default App;
